;; @@
(defn score-pp
  [data ex pressure-coeff]
  (+ (score data ex) (* pressure-coeff (count-nodes ex))))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;user/score-pp</span>","value":"#'user/score-pp"}
